export function getPlatform() {
  if (isUcf()) {
    return 'ucf';
  } else {
    return 'web';
  }
}
export function isUcf() {
  var pattern = /ucf/;
  if (navigator && pattern.test(navigator.userAgent.toLowerCase())) {
    // 增加跨平台的保护，避免客户端没有实现 ucf 的跨平台接口
    return window.usdk ? true : false;
  } else {
    return false;
  }
}